package _ch03;

public class Ex3_4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = -10;   // i = -10
		i = +i;        // i = +(-10)
		System.out.println(i);
	    //  부호 연산자 + 는 아무일도 안함
		
		i = -10;     // i = -10
		i = -i;      // i = - (-10)
		System.out.println(i);
		// 부호 연산자 -는 피연산자의 부호를 반대로 변경
		
		i = 10;      // i = 10
		i = -i;  //  i = - (10)
		
		//
		System.out.println(i);
		
	}

}
